import React, { useEffect, useState } from "react";
import './style.css'

function Wordle() {
    const height = 4; // number of guesses
    const width = 4;

    let row = 0; // this number represents the current word we are guessing
    let column = 0; // represents the current letter for each attempt

    let gameOver = false;

    const objects = [
        {
            word: "appl",
            hint: "A fruit that keeps the doctor away.",
        },
        {
            word: "elep",
            hint: "The largest land animal.",
        },
        {
            word: "ocea",
            hint: "A vast body of salt water.",
        },
        {
            word: "guit",
            hint: "A musical instrument with strings.",
        },
        {
            word: "pyra",
            hint: "A triangular structure in Egypt.",
        },
        {
            word: "moun",
            hint: "A large natural elevation of the earth's surface.",
        },
        {
            word: "comp",
            hint: "An electronic device for storing and processing data.",
        },
        {
            word: "rain",
            hint: "A spectrum of light in the sky.",
        },
        {
            word: "butt",
            hint: "An insect with colorful wings.",
        },
        {
            word: "libr",
            hint: "A place with a collection of books.",
        },
    ];

    const [wordoftheday, setWordOfTheDay] = useState({});
    const [word, setWord] = useState("");
    const [hint, setHint] = useState("");

    useEffect(() => {
        const randomObject = objects[Math.floor(Math.random() * objects.length)];
        setWordOfTheDay(randomObject);
        setWord(randomObject.word.toUpperCase());
        setHint(randomObject.hint);
        console.log(randomObject.word.toUpperCase());
        console.log(randomObject.hint);
        alert("Click the info icon to toggle the instructions.");
        createboard();
    }, []);

    function createboard() {
        document.getElementById("changetheme").addEventListener("click", todark);
        document.getElementById("info").addEventListener("click", toggleinfo);
        document.getElementById("startover").addEventListener("click", () => {
            window.location.reload();
        });
        document.getElementById("help").addEventListener("click", displayHint);

        for (let r = 0; r < height; r++) {
            for (let c = 0; c < width; c++) {
                let tile = document.createElement("span");
                tile.id = r.toString() + "-" + c.toString();
                tile.classList.add("tile"); // This adds the tile class in the style sheet to the tile element.
                tile.innerText = "";
                document.getElementById("board").appendChild(tile);
            }
        }

        // Now we add code to listen for user input
        document.addEventListener("keyup", (e) => {
            // We use key up here so that letters are only registered when a key comes up after pushing it down. (To avoid spamming)
            if (gameOver) return; // Note that (e) is just a parameter for an actual pressed key
            // processInput(e);
            if ("KeyA" <= e.code && e.code <= "KeyZ") {
                if (column < width) {
                    let currTile = document.getElementById(row.toString() + "-" + column.toString());
                    if (currTile.innerText === "") {
                        currTile.innerText = e.code[3];
                        column += 1;
                    }
                }
            } else if (e.code === "Backspace") {
                if (0 < column && column <= width) {
                    column -= 1;
                }
                // Because we went back one tile, we need to update the tile id
                let currTile = document.getElementById(row.toString() + "-" + column.toString());
                currTile.innerText = ""; // so if backspace is pressed, we change the value of the current tile to nothing and we decrement column by 1.
            } else if (e.code === "Enter") {
                if (column !== width) {
                    alert("first complete the word");
                } else if (column === width) {
                    update(); // we call an update function that will process the contents of that row and change the colors of the boxes.
                    row += 1; // fresh row
                    column = 0; // first column in fresh row
                }
            }

            if (!gameOver && row === height) {
                gameOver = true;
                document.getElementById("answer").innerText = word; // adds the word to the header tag with the id "answer". Note that "answer" can also be styled in the css using its id.
                document.getElementById("bottom").innerText = "You missed the word " + word + " and lost!";
                document.getElementById("bottom").style.backgroundColor = "darkred";
                document.getElementById("bottom").style.color = "white";
                document.getElementById("vertical_line").style.display = "none";
                document.getElementById("bottom").style.marginTop = "14%";
            }
        });
    }

    function update() {
        let correctletters = 0;
        for (let i = 0; i < width; i++) {
            let currTile = document.getElementById(row.toString() + "-" + i.toString());
            let letter = currTile.innerText;

            // if the letter is in the correct position, we increase the number of correct letters
            if (word[i] === letter) {
                currTile.classList.add("correct"); // adds the "correct" style class to the tile if it is the right position
                correctletters += 1;
            } else if (word.includes(letter)) {
                currTile.classList.add("present");
            } else {
                currTile.classList.add("absent");
            }
        }
        if (correctletters === width) {
            gameOver = true;
            document.getElementById("bottom").innerText = "You guessed the word " + word + " correctly!";
            document.getElementById("bottom").style.backgroundColor = "yellowgreen";
            document.getElementById("vertical_line").style.display = "none";
            document.getElementById("board").style.display = "none";
            document.getElementById("infotext").style.display = "none";
            document.getElementById("congratulations").style.display = "flex";
            document.getElementById("bottom").style.marginTop = "2%";
        }
    }

    function todark() {
        var something = document.body;
        something.classList.toggle("dark");
        var boardtext = document.getElementById("board");
        boardtext.classList.toggle("whitetext");
    }

    function toggleinfo() {
        var info = document.getElementById("infotext");
        info.classList.toggle("display");

        var line = document.getElementById("vertical_line");
        line.classList.toggle("display");
    }

    function displayHint() {
        setHint(hint)
        document.getElementById("bottom").innerText = "Hint: " + hint;
        document.getElementById("bottom").style.backgroundColor = "beige";
        document.getElementById("vertical_line").style.display = "none";
        document.getElementById("bottom").style.color = "black";
    }

    return (
    <body>
        <navbar><div><h1>Wordle</h1></div><div id = "buttons"><div id="changetheme">&#9681;</div><div id="help">&#63;</div><div id="info">&#9432;</div></div></navbar>
        <hr/> 




    <div class = "fullbody">
      <div id = "board"></div>
      <div id = "congratulations" style = {{display:"none"}}><img src ="https://res.cloudinary.com/mkf/image/upload/v1675467141/ENSF-381/labs/congrats_fkscna.gif"/></div>
      <div id = "vertical_line"></div>
      <div id = "infotext">
        <h3>How To Play</h3>
        <p></p>
        <ul>
          <li>Start typing. The letters will appear in the boxes.</li>
          <li>Remove letters with backspace.</li>
          <li>Hit Enter/Return to submit an answer.</li>
          <li>Letters with green background are in the right spot.</li>
          <li>Letters with yellow background exist in the word, but are in the wrong spots.</li>
          <li>Letters with gray background do not exist in the word.</li>
          <li>If you need a hint, click the &#63 icon.</li>

        </ul>
      </div>


    </div>
    <div id = "startover">Start Over</div>


        6GJ,
    <h1 id ="answer"></h1>

    <section id = "bottom"></section>


    <footer class = "foot">
      <p>&#169 Emiko Wordle 2023</p>
    </footer>

  </body>
    );
}

export default Wordle;
